
public class TipeData {

    public static void main(String[] args) {
        System.out.println("Hello World");

        //pemisah
        System.out.println(" ");

        //Tipe data String
        String nama = ("Muhammad Arief Furqany");
        System.out.println(nama);
        System.out.println("ini adalah tipe data String");

        //pemisah
        System.out.println(" ");

        ///Tipe data char
        Char tipeChar = ('a');
        System.out.println(tipeChar);
        System.out.println("ini adalah tipe data Char");

        //pemisah
        System.out.println(" ");

        //Tipe data double
        double ipk = 3.82;
        System.out.println(ipk);
        System.out.println("ini adalah tipe data double");

        //pemisah
        System.out.println(" ");

        //Tipe data Float
        Float TipeFLoat = 50.7f;
        System.out.println(TipeFLoat);
        System.out.println("ini adalah tipe data float");

        //pemisah
        System.out.println(" ");

        //Tipe data integer(Int)
        int nim = 220705043;
        System.out.println(nim);
        System.out.println("Ini adalah tipe data Integer(int)");

        //pemisah
        System.out.println(" ");
        
        //tipe data long (tipe data dengan tampungan lebih banyak daripada integer)
        long tipeLong = 7238;
        System.out.println(tipeLong);
        System.out.println("ini adalah tipe data long");

        //Tipe data Boolean
        Boolean TipeBoolean = true;

        //pemanfaatan boolean dalam if statement
        int a = 2;

        if (a == 5) {
            System.out.println("adalah benar");
        } else {
            System.out.println("adalah mitos");
        }
        System.out.println("ini adalah percabangan dengan inputan true");





    }
    
}
